operation.add:
while ($row = $smcFunc['db_fetch_assoc']($request))
{
	$icon_code = base64_encode($row['filename']);
	if(isset($context['icons'][$icon_code]['board_id']))
	{
		$old_boards = $context['icons'][$icon_code]['board_id'];
		$old_view = $context['icons'][$icon_code]['view_in_all'];
	}
	else
	{
		$old_boards = null;
		$old_view = null;
	}

	$icon_ids[$icon_code][$row['id_board']] = $row['id_icon'];
	$context['icons'][$icon_code] = array(
		'id' => $row['id_icon'],
		'icon_code' => $icon_code,
		'title' => $row['title'],
		'filename' => $row['filename'],
		'image_url' => $settings[file_exists($settings['theme_dir'] . '/images/post/' . $row['filename'] . '.gif') ? 'actual_images_url' : 'default_images_url'] . '/post/' . $row['filename'] . '.gif',
		'board_id' => $row['id_board'],
		'board' => empty($row['board_name']) ? $txt['icons_edit_icons_all_boards'] : $row['board_name'],
		'order' => $row['icon_order'],
	);

	if (isset($old_view))
		$context['icons'][$icon_code]['view_in_all'] = empty($row['id_board']) ? true : $old_view;
	else
		$context['icons'][$icon_code]['view_in_all'] = empty($row['id_board']);

	if (isset($old_boards) && is_array($old_boards))
	{
		$context['icons'][$icon_code]['board_id'] = $old_boards;
		$context['icons'][$icon_code]['board_id'][] = $row['id_board'];
	}
	else
		$context['icons'][$icon_code]['board_id'] = array($row['id_board']);
}
$smcFunc['db_free_result']($request);

$last_icon = 0;
$trueOrder = 0;
foreach ($context['icons'] as $id => $data)
{
	$context['icons'][$id]['true_order'] = $trueOrder++;
	$context['icons'][$id]['after'] = $last_icon;
	$last_icon = $id;
}

